import React from "react";
import { Page } from "../components/Page";
import { AnimatedText } from "../components/Interaction";
import { Sponsor } from "./Sponsor";
import { Partner } from "./Partner";
import { CoC } from "./CoC";
import { FAQ } from "./FAQ";
import { Speaker } from "./Speaker";
import Hero from "../img/hero.svg";
import backgroundVideo from "../bg_video_final.mp4";
import bgPoster from "../bg_poster.webp";

import data from "../data/talks";

export function Home() {
  const spring = {
    type: "spring",
    damping: 1,
    stiffness: 100,
  };

  const aboutRef = React.useRef();
  const partnerRef = React.useRef();
  const programRef = React.useRef();
  const cocRef = React.useRef();

  console.log(aboutRef);

  const size = useWindowSize();

  return (
    <>
      <div
        className="video-container"
        dangerouslySetInnerHTML={{
          __html: `

          <video
            autoplay
            playsinline
            loop
            preload="auto"
            muted
            poster="${bgPoster}"
            style=${
              size.width >= size.height ? `"width: 100%;"` : `"height: 100%;"`
            }
          >
            <source src="${backgroundVideo}" type="video/mp4" />
          </video>`,
        }}
      />

      <div style={{ position: "fixed", top: 0, zIndex: 100 }}>
        <ScrollItem ref={aboutRef.current.offsetTop} text={"About"} />
        <ScrollItem ref={partnerRef.current.offsetTop} text={"Partner"} />
        <ScrollItem ref={programRef.current.offsetTop} text={"Program"} />
        <ScrollItem ref={cocRef.current.offsetTop} text={"CoC"} />
      </div>

      <Page center={true}>
        <div className="title">
          <img
            src={Hero}
            style={{ width: "100%", height: "50vh" }}
            alt="Hero"
          />
        </div>
      </Page>
      <div className="content" style={{ marginTop: 40 }} ref={aboutRef}>
        <Page center={size.width > 480 ? true : false}>
          <div className="title">
            <AnimatedText delay={0.4} yPos={40} spring={spring}>
              <div className="heading-1">A Whole New World</div>
            </AnimatedText>
          </div>

          <div className="content-wrapper-2">
            <div style={{ display: "grid", gridGap: 40 }}>
              <div className="content-paragraph">
                <AnimatedText delay={0.8} yPos={0}>
                  <div className="heading-2">새로운 형식</div>
                </AnimatedText>
                <AnimatedText delay={0.8} yPos={0}>
                  <div className="body-1">
                    코로나19로 열린 비대면 시대. <br />
                    2020 스펙트럼콘은 새로운 형식의 온라인 콘퍼런스를
                    시도합니다. {size.width >= 640 && <br />}이번 콘퍼런스는
                    온라인으로, 2주 동안 네 가지 주제로 진행합니다.{" "}
                    {size.width >= 640 && <br />}퀄리티 높은 온라인 경험을
