import React, { Suspense, useRef } from "react";
import { Canvas, useFrame, useLoader } from "react-three-fiber";
import { GLTFLoader } from "three/examples/jsm/loaders/GLTFLoader";
// import { OrbitControls } from "drei";

function LoadModel({ props, url, pos, value, r, g, b }) {
    const { scene } = useLoader(GLTFLoader, url);
    useFrame(
      () =>
        (ref.current.rotation.x = ref.current.rotation.z = ref.current.rotation.y += value)
    );
    const ref = useRef();

    scene.children[0].material.wireframe = true;
    scene.children[0].material.color.r = r;
    scene.children[0].material.color.g = g;
    scene.children[0].material.color.b = b;

    return (
      <group {...props} dispose={null} ref={ref}>
        <group position={pos} rotation={[1, 0.5, -0.6]} scale={[0.2, 0.2, 0.2]}>
          <primitive object={scene} scale={[10, 10, 10]}/>
        </group>
      </group>
    );
  }

  export function Glitch() {
    return (
      <Canvas camera={{ position: [-100, -100, -100] }}>
        <ambientLight intensity={1} />
        <pointLight position={[-400, -400, -400]} />
        <Suspense fallback={null}>
          <LoadModel url="./obj1.glb" pos={[0, 0, 0]} value={.01} r={0} g={0} b={255} />
          <LoadModel url="./obj2.glb" pos={[100, 100, 100]} value={.001}/>
          <LoadModel url="./obj3.glb" pos={[0, 100, 100]} value={.002}/>
          <LoadModel url="./obj4.glb" pos={[100, -100, 0]} value={.003}/>
        </Suspense>
      </Canvas>
    );
  }

  export function GlitchPage() {
      return(
        <div className="container-glitch">
            <div className="container-bg-text">
                <div className="bg-text-test">
                    3D INTERACTION TO BE DONE VERY SOON... VOILA
                </div>
            </div>
            <div className="canvas">
                <Glitch />
            </div>
        </div>
      )
  }
