import React from "react";
import { GlitchPage } from "../components/Glitch";
import { Page } from "../components/Page";
import { motion, useAnimation } from "framer-motion";

function AnimatedTitle() {
  const controls = useAnimation();

  React.useEffect(() => {
    controls.start((i) => ({
      opacity: 1,
      y: 0,
      transition: { duration: .5, delay: i * 0.3 },
    }));
  }, []);

  return (
    <div className="title" style={{ overflow: "hidden" }}>
      <motion.div custom={0} initial={{ y: 200, opacity: 0 }} animate={controls}>
        <div className="heading-1">Spectrum Con</div>
      </motion.div>
      <motion.div custom={1} initial={{ y: 200, opacity: 0 }} animate={controls}>
        <div className="heading-1">Design Week 2020</div>
      </motion.div>
    </div>
  );
}

export function TeaserHome() {
  return (
    <>
      <GlitchPage />
      <div className="content">
        <Page center={true}>
          <AnimatedTitle />
          <div
            style={{
              display: "grid",
              // gridTemplateColumns: "auto max-content",
              gridGap: 95,
              maxWidth: 940,
            }}
          >
            <div
              style={{
                display: "grid",
                gridGap: 8,
              }}
            >
              <div className="heading-2">Nov 23, 2020 - Dec 4, 2020</div>
              <div className="body-1">
                디자이너들이 자신의 가치를 확인하고 더욱 발전하기 위해서는 여러
                각도에서의 자극과 탐구가 필요합니다. 그것이 온라인이든
                오프라인이든 상관없이 디자이너들이 이러한 필요를 느낄 때
                자유롭게 찾아와서 이야기할 수 있는 기회와 공간이 필요하다고
                생각했습니다.
              </div>
            </div>
          </div>
        </Page>
      </div>
    </>
  );
}
