import React from "react";
import "../components/Speaker.css";
import { BrowserRouter as Router, NavLink } from "react-router-dom";

import { motion } from "framer-motion";
import { Page } from "../components/Page";
import BG1 from "../img/bg1.svg";
import BG2 from "../img/bg2.svg";
import BG3 from "../img/bg3.svg";

import { SpeakerList } from "../components/SpeakerList"

export function Speaker({ data, myRef }) {
    return (
      <Page center={false} myRef={myRef} marginBottom={160}>
        <div className="title">
          <div className="heading-1">Program</div>
        </div>
        <div className="body-1">2주간 진행되는 다양한 세션을 들어보세요.</div>
        <SpeakerList data={data} />

        <img
          style={{
            position: "absolute",
            top: 0,
            left: -480,
            pointerEvents: "none",
            zIndex: -10
          }}
          src={BG1}
          alt={"bg3"}
        />
        <img
          style={{
            position: "absolute",
            top: 820,
            left: -800,
            pointerEvents: "none",
            zIndex: -10
          }}
          src={BG2}
          alt={"bg3"}
        />
        <img
          style={{
            position: "absolute",
            top: 440,
            left: 460,
            pointerEvents: "none",
            zIndex: -10
          }}
          src={BG3}
          alt={"bg3"}
        />
      </Page>
    );
  }
