import React from "react";
import { Page } from "../components/Page";
import FAQData from "../data/faq";
import {motion} from "framer-motion"

export function FAQ({ myRef }) {
  return (
    <Page center={false} myRef={myRef} marginBottom={240}>
      <div className="title">
        <div className="heading-1">FAQ</div>
      </div>
      <div style={{ display: "grid" }}>
        {FAQData.map((x, i) => {
          return (
            <div key={i} style={{padding: "24px 8px", borderTop: "1px solid #C6CED5"}}>
            <div style={{display: "grid", gridTemplateColumns: "auto 32px"}}>
            <div className="heading-2">{x.title}</div>

            </div>  
              <div className="body-1">{x.text}</div>
            </div>
          );
        })}
      </div>
    </Page>
  );
}
