import React from "react";
import "./Speaker.css";
import { BrowserRouter as Router, NavLink } from "react-router-dom";

import { motion } from "framer-motion";

import { SpeakerCard } from "./SpeakerCard";

export function SpeakerList({ data }) {
  const [weekone, setWeekone] = React.useState(true);
  const [weektwo, setWeektwo] = React.useState(false);

  return (
    <div className="speaker-list-wrapper">
      <div className="speaker-list-nav">
        <div
          className={
            weekone
              ? "speaker-list-nav-list-selected"
              : "speaker-list-nav-list-unselected"
          }
          onClick={() => {
            setWeekone(!weekone);
            setWeektwo(!weektwo);
          }}
          initial={weekone ? { opacity: 1 } : { opacity: 0.4 }}
          whileHover={{ opacity: 1 }}
        >
          Week 1 Nov 23 - Nov 29
        </div>
        <div
          className={
            weektwo
              ? "speaker-list-nav-list-selected"
              : "speaker-list-nav-list-unselected"
          }
          onClick={() => {
            setWeekone(!weekone);
            setWeektwo(!weektwo);
          }}

        >
          Week 2 Dec 1 - Dec 6
        </div>
      </div>
      <div className="speaker-list-container">
        <div className="speaker-list-left">
          {data
            .filter((x, i) => i % 2 === 0)
            .map((x, i) => {
              return (
                <NavLink to={`/${x.link}`} key={i}>
                  <SpeakerCard data={x} />
                </NavLink>
              );
            })}
        </div>
        <div className="speaker-list-right">
          {data
            .filter((x, i) => i % 2 === 1)
            .map((x, i) => {
              return (
                <NavLink to={`/${x.link}`} key={i}>
                  <SpeakerCard data={x} />
                </NavLink>
              );
            })}
        </div>
      </div>
    </div>
  );
}
